    isPatient: function isPatient() {
      var _this = this;
      if (this.getRolePatient()) {
        PatientService.getPatientByUserId(this.$store.state.user.id).then(function (response) {
          _this.$store.commit("SET_PATIENT", response.data);
        });
        PatientService.getCurrentPatientId(this.$store.state.user.id).then(function (response) {
          _this.$store.commit("SET_PATIENTID", response.data);
        });
      }
    }
  },
  computed: {
    getRoleAdmin: function getRoleAdmin() {
      return this.$store.state.user.authorities[0].name === 'ROLE_ADMIN';
    },
    getRolePatient: function getRolePatient() {
      return this.$store.state.user.authorities[0].name === 'ROLE_USER';
    },
    getRoleDoctor: function getRoleDoctor() {
      return this.$store.state.user.authorities[0].name === 'ROLE_DOCTOR';
    }
  },
