import patientService from "../services/PatientService.js";
export default {
  name: "patient-list",
  computed: {
    headers: function headers() {
      return [{
        text: "First Name",
        value: "firstName"
      }, {
        text: "Last Name",
        value: "lastName"
      }, {
        text: "Email Address",
        value: "emailAddress"
      }, {
        text: "City",
        value: "city"
      }, {
        text: "State",
        value: "states"
      }];
    },
    getDoctorId: function getDoctorId() {
      return this.$store.getters.getDoctorId;
    }
  },
  watch: {
    getDoctorId: "refreshPatientList" // Watch for changes in getDoctorId and call getPatients
  },

  methods: {
    getPatients: function getPatients() {
      var _this = this;
      var doctorId = this.$store.getters.getDoctorId;
      patientService.getPatientByBookedAppointment(doctorId).then(function (response) {
        _this.$store.commit("SET_PATIENTS", response.data);
      });
    },
    refreshPatientList: function refreshPatientList() {
      this.getPatients();
    },
    goToUpdateForm: function goToUpdateForm() {
      this.item = "";
      this.$router.push({
        name: "updateDoctorAppointments"
      });
    }
  },
  data: function data() {
    return {
      patients: []
    };
  },
  created: function created() {
    this.getPatients();
  }
};
